<svg
  width="460"
  height="110"
  xmlns="http://www.w3.org/2000/svg"
  xmlns:xlink="http://www.w3.org/1999/xlink">
  <text class="arc-font" text-anchor="middle" x="230" y="110" fill="black">ARC</text>
  <style>
    @import url(data:font/truetype;charset=utf-8;base64,);
    .arc-font {
      font-family: Blank;
      font-size: 200px;
    }

    svg text {
      text-transform: uppercase;
      animation: stroke 5s infinite alternate;
      stroke-width: 2;
      stroke: #FFFFFF;
    }
    @keyframes stroke {
      0%   {
        fill: rgba(255,255,255,0); stroke: rgba(255,255,255,1);
        stroke-dashoffset: 25%; stroke-dasharray: 0 50%; stroke-width: 2;
      }
      70%  {fill: rgba(255,255,255,0); stroke: rgba(255,255,255,1); }
      80%  {fill: rgba(255,255,255,0); stroke: rgba(255,255,255,1); stroke-width: 3; }
      100% {
        fill: rgba(255,255,255,1); stroke: rgba(255,255,255,0); 
        stroke-dashoffset: -25%; stroke-dasharray: 50% 0; stroke-width: 0;
      }
    }
  </style>
</svg>